<?php/** * Template Name: Forte50 Principals Page*//** * Description: Principals (Team) page * list the posts categorized as "Principals" in ascending MENU ORDER, excerpt view * @since admired 1.0 */get_header(); ?>		<div id="primary">			<div id="content" role="main"><?php  //output the page preface content$args = array( 'page_id' => '8');$loop = new WP_Query( $args );while ( $loop->have_posts() ) {  $loop->the_post();  get_template_part( 'loop', 'page' );}?><?php/* loop through individual principals/bios */$args = array( 'cat' => '3', 'order_by' => 'menu_order', 'order' => 'ASC');$loop = new WP_Query( $args );while ( $loop->have_posts() ) : $loop->the_post();?><article id="post-<?php the_ID(); ?>" <?php post_class(); ?> ><?php$pic_width = 300;$pic_height = 300;if( has_post_thumbnail() ) :?><div class="resource-img">  <?php the_post_thumbnail( array($pic_width,$pic_height) ); ?></div><?php endif; ?><div class="resource-content">	<header class="entry-header">		<h2 class="entry-title"><?php the_title(); ?></h2>	</header><!-- .entry-header -->	<div class="entry-content">    <?php the_content(); ?>		<?php wp_link_pages( array( 'before' => '<div class="page-link"><span>' . __( 'Pages:', 'admired' ) . '</span>', 'after' => '</div>' ) ); ?>	</div><!-- .entry-content -->	<footer class="entry-meta" style="clear:both;">	</footer><!-- .entry-meta --></div><!-- .resource-content--><div style="clear:both;"></div></article><!-- #post-<?php the_ID(); ?> --><?php endwhile;?>			</div><!-- #content -->		</div><!-- #primary --><?php get_sidebar(); ?><?php get_footer(); ?>