<?php/** * Template Name: Forte50 Individual Post Pages*//** * Description: Individual Post pages, determined by category type  * Business Intelligence, category_id = 7  * Forte Findings, category_id = 8  * Principals, category_id = 3  **** Not implemented yet ****  * Blog, category_id = 5  * News, category_id = 4 * @since admired 1.0 */get_header(); ?>		<div id="primary">			<div id="content" role="main"><?php while ( have_posts() ) : the_post(); ?><article id="post-<?php the_ID(); ?>" <?php post_class(); ?> ><?php if( in_category( 3 )  ) :?><?php$pic_width = 300;$pic_height = 300;if( has_post_thumbnail() ) :?><div class="resource-img">  <?php the_post_thumbnail( array($pic_width,$pic_height) ); ?></div><?php endif; /* if thumbnail exists */ ?><?php endif; /* if in "principles" category */ ?><div class="resource-content">	<header class="entry-header">		<h2 class="entry-title"><?php the_title(); ?></h2><?php if( !in_category( 3 )  ) : ?>		<div class="entry-meta">			<?php admired_posted_on(); ?>		</div><!-- .entry-meta --><?php endif; /* if not in "principles" category */ ?>	</header><!-- .entry-header -->	<div class="entry-content">    <?php the_content(); ?>		<?php wp_link_pages( array( 'before' => '<div class="page-link"><span>' . __( 'Pages:', 'admired' ) . '</span>', 'after' => '</div>' ) ); ?>	</div><!-- .entry-content -->	<footer class="entry-meta" style="clear:both;">	</footer><!-- .entry-meta --></div><!-- .resource-content--><div style="clear:both;"></div></article><!-- #post-<?php the_ID(); ?> --><?php endwhile;?>			</div><!-- #content -->		</div><!-- #primary --><?php get_sidebar(); ?><?php get_footer(); ?>